package cn.com.cunw.diandubiapp.base.popup;import android.support.annotation.IntDef;import java.lang.annotation.Retention;import java.lang.annotation.RetentionPolicy;/** * @author LiuJianjun * @time 2018-09-19 11:40 * <p> * 功能： */@IntDef({     HorizontalGravity.CENTER,     HorizontalGravity.LEFT,     HorizontalGravity.RIGHT,     HorizontalGravity.ALIGN_LEFT,     HorizontalGravity.ALIGN_RIGHT})@Retention(RetentionPolicy.SOURCE)public @interface HorizontalGravity {    int CENTER = 0;    int LEFT = 1;    int RIGHT = 2;    int ALIGN_LEFT = 3;    int ALIGN_RIGHT = 4;}